sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var whatneeded = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');
	if(newValue=='hr'){

		g_form.addOption('u_whatneeded','hr1','human resorces1');
		g_form.addOption('u_whatneeded','hr2','human resorces2');
		g_form.addOption('u_whatneeded','others','others');
	}

	if(newValue=='facilities'){

		g_form.addOptions('u_whatneeded','facilities1','facility1');
		g_form.addOptions('u_whatneeded','facilities2','facility2');
		g_form.addOption('u_whatneeded','others','others');
	}
	if(newValue=='legal'){
		g_form.addOptions('u_whatneeded','legal1','legal1');
		g_form.addOptions('u_whatneeded','legal2','legal2');
		g_form.addOptions('u_whatneeded','others','others');

	}

}
